import React, { useEffect, useState } from 'react';
import HabitCard from '../components/HabitCard';
import { getHabits } from '../services/api';
import BaseLayout from '../components/BaseLayout';
import Navbar from '../components/Navbar';
import '../styles/mobile.css';

const Home = () => {
    const [habits, setHabits] = useState([]);
    const [error, setError] = useState(null);

    const token = localStorage.getItem('token');

    const fetchHabits = async () => {
        if (token) {
            try {
                const data = await getHabits(token);
                setHabits(Array.isArray(data) ? data : []);
            } catch (error) {
                console.error('Error al obtener los hábitos:', error);
                setError('Hubo un problema al obtener los hábitos. Inténtalo más tarde.');
            }
        } else {
            setError('No se encontró un token de sesión. Inicia sesión nuevamente.');
        }
    };

    useEffect(() => {
        fetchHabits();
    }, [token]);

    return (
        <>
            <BaseLayout title="Bienvenido a Tu App de Hábitos">
                {error && <div className="error-message">{error}</div>}
                <div className="habit-list">
                    {habits.length > 0 ? (
                        habits.map((habit) => (
                            <HabitCard key={habit._id} habit={habit} token={token} onHabitUpdated={fetchHabits} />
                        ))
                    ) : (
                        <p>No hay hábitos disponibles. ¡Crea uno para empezar!</p>
                    )}
                </div>
            </BaseLayout>
            <Navbar />
        </>
    );
};

export default Home;
