import React, { useEffect, useState } from 'react';
import HabitCard from '../components/HabitCard';
import { getHabits, completeHabit } from '../services/api';

const Home = () => {
    const [habits, setHabits] = useState([]);
    const token = localStorage.getItem('token'); // Obtiene el token de localStorage

    useEffect(() => {
        const fetchHabits = async () => {
            if (token) {
                const data = await getHabits(token);
                setHabits(data);
            }
        };
        fetchHabits();
    }, [token]);

    const handleComplete = async (id) => {
        await completeHabit(id, token);
        setHabits(habits.map(habit => habit._id === id ? { ...habit, completed: true } : habit));
    };

    return (
        <div>
            <h1>Tus Hábitos</h1>
            {habits.length > 0 ? (
                habits.map(habit => (
                    <HabitCard key={habit._id} habit={habit} onComplete={handleComplete} />
                ))
            ) : (
                <p>No tienes hábitos para mostrar. ¡Empieza agregando algunos!</p>
            )}
        </div>
    );
};

export default Home;
