import React from 'react';
import '../styles/mobile.css';
import seed from '../assets/seed.png';
import sapling from '../assets/sapling.png';
import youngTree from '../assets/young_tree.png';
import tree from '../assets/tree.png';

const worldBackgrounds = {
    forest: '/images/forest_bg.png',
    city: '/images/city_bg.png',
    ocean: '/images/ocean_bg.png',
};

const ProgressTree = ({ progress, world }) => {
    const getTreeStage = () => {
        if (progress < 25) return seed;
        if (progress < 50) return sapling;
        if (progress < 75) return youngTree;
        return tree;
    };

    const backgroundStyle = {
        backgroundImage: `url(${worldBackgrounds[world] || worldBackgrounds.forest})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        padding: '20px',
        borderRadius: '15px',
    };

    return (
        <div style={backgroundStyle} className="tree-container">
            <img src={getTreeStage()} alt="Árbol de progreso" className="tree-image" />
            <p className="tree-progress-label">Progreso: {progress}%</p>
        </div>
    );
};

export default ProgressTree;
