import React from 'react';
import ProgressTree from './ProgressTree';
import '../styles/neomorph.css';

const HabitCard = ({ habit, onComplete }) => {
    const { title, description, progress, world } = habit;

    const worldBackground = {
        'selva': '/images/jungle-background.jpg',
        'ciudad': '/images/city-background.jpg',
    }[world] || '/images/default-background.jpg';

    return (
        <div className="neomorph-card" style={{ backgroundImage: `url(${worldBackground})`, backgroundSize: 'cover' }}>
            <h3>{title}</h3>
            <p>{description}</p>
            <ProgressTree progress={progress} />
            <button className="neomorph-button" onClick={onComplete}>Marcar como Completo</button>
        </div>
    );
};

export default HabitCard;
