import React from 'react';
import { updateHabitWorld } from '../services/api';

const HabitCard = ({ habit, token, onHabitUpdated }) => {
    const handleUpdateWorld = async () => {
        try {
            const updatedData = { world: habit.world }; // Datos actualizados según corresponda
            await updateHabitWorld(habit._id, updatedData, token);
            onHabitUpdated(); // Llama a la función para actualizar los hábitos en la UI
        } catch (error) {
            console.error('Error al actualizar el mundo del hábito:', error);
            alert('Hubo un problema al actualizar el mundo del hábito. Por favor, inténtalo de nuevo.');
        }
    };

    return (
        <div className="habit-card">
            <h3>{habit.name}</h3>
            <button onClick={handleUpdateWorld} className="neomorph-button">Actualizar Mundo</button>
        </div>
    );
};

export default HabitCard;
