import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { registerUser } from '../services/api';

const Register = () => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        const userData = { username, email, password };

        try {
            const data = await registerUser(userData);
            localStorage.setItem('token', data.token);
            localStorage.setItem('user', JSON.stringify(data));
            navigate('/');
            window.location.reload();
        } catch (err) {
            const errorMsg = err.response?.data?.message || 'Error al registrarse';
            setError(errorMsg);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="neomorph-form">
            <h2>Registro</h2>
            {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}
            <input 
                type="text" 
                placeholder="Nombre de Usuario" 
                value={username} 
                onChange={(e) => setUsername(e.target.value)} 
                required 
                className="neomorph-input"
            />
            <input 
                type="email" 
                placeholder="Email" 
                value={email} 
                onChange={(e) => setEmail(e.target.value)} 
                required 
                className="neomorph-input"
            />
            <input 
                type="password" 
                placeholder="Contraseña" 
                value={password} 
                onChange={(e) => setPassword(e.target.value)} 
                required 
                className="neomorph-input"
            />
            <button type="submit" className="neomorph-button">Registrarse</button>
            <p style={{ textAlign: 'center', marginTop: '10px' }}>
                ¿Ya tienes una cuenta? <Link to="/login" style={{ color: '#6fcf97' }}>Inicia sesión aquí</Link>
            </p>
        </form>
    );
};

export default Register;
