import React from 'react';
import BaseLayout from '../components/BaseLayout';
import Navbar from '../components/Navbar';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
    const user = JSON.parse(localStorage.getItem('user'));
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login');
        window.location.reload();
    };

    return (
        <>
            <BaseLayout title={`Perfil de ${user?.username || 'Usuario'}`}>
                {user ? (
                    <div className="profile-content">
                        <p><strong>Email:</strong> {user.email}</p>
                        <p><strong>Rango:</strong> {user.rank || 'Sin rango'}</p>
                        <p><strong>Puntos:</strong> {user.points || 0}</p>
                        <button className="neomorph-button" onClick={() => navigate('/edit-profile')}>
                            Editar Perfil
                        </button>
                    </div>
                ) : (
                    <p>Usuario no disponible.</p>
                )}
                <button onClick={handleLogout} className="neomorph-button">
                    Cerrar Sesi√≥n
                </button>
            </BaseLayout>
            <Navbar />
        </>
    );
};

export default Profile;
