import React, { useEffect, useState } from 'react';
import HabitCard from '../components/HabitCard';
import Rewards from '../components/Rewards';
import VirtualPet from '../components/VirtualPet';
import { getHabits, completeHabit } from '../services/api';

const Home = () => {
    const [habits, setHabits] = useState([]);
    const [points, setPoints] = useState(0);
    const [petHealth, setPetHealth] = useState(100);
    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchHabits = async () => {
            if (token) {
                const data = await getHabits(token);
                setHabits(data);
            }
        };
        fetchHabits();
    }, [token]);

    const handleComplete = async (id) => {
        await completeHabit(id, token);
        setPoints(points + 10); 
        setPetHealth(Math.min(100, petHealth + 5)); 
        setHabits(habits.map(habit => habit._id === id ? { ...habit, completed: true } : habit));
    };

    const handleRedeem = () => {
        if (points >= 100) { 
            alert('Has canjeado tus puntos por una recompensa visual');
            setPoints(points - 100); 
        } else {
            alert('No tienes suficientes puntos para canjear');
        }
    };

    return (
        <div className="page-content">
            <h1>Bienvenido a Tu App de HÃ¡bitos</h1>
            <Rewards points={points} onRedeem={handleRedeem} />
            <VirtualPet petHealth={petHealth} />
            <div>
                {habits.map(habit => (
                    <HabitCard key={habit._id} habit={habit} onComplete={() => handleComplete(habit._id)} />
                ))}
            </div>
        </div>
    );
};

export default Home;
