import React from 'react';
import seed from '../images/progress/seed.png';
import sapling from '../images/progress/sapling.png';
import youngTree from '../images/progress/young_tree.png';
import tree from '../images/progress/tree.png';
import '../styles/mobile.css';

const ProgressTree = ({ habits = [] }) => {
    const calculateProgress = (habit) => {
        // Placeholder calculation for habit progress
        return habit.progress || 0;
    };

    const getTreeImage = (progress) => {
        if (progress < 25) {
            return seed;
        } else if (progress < 50) {
            return sapling;
        } else if (progress < 75) {
            return youngTree;
        } else {
            return tree;
        }
    };

    if (!Array.isArray(habits)) {
        console.error('Expected habits to be an array but got:', habits);
        return <div className="error-message">Hubo un error al cargar los hábitos. Por favor, inténtalo de nuevo más tarde.</div>;
    }

    return (
        <div className="progress-tree-container">
            {habits.length === 0 ? (
                <p>No hay hábitos disponibles. ¡Crea uno para empezar!</p>
            ) : (
                habits.map((habit) => {
                    const progress = calculateProgress(habit);
                    const treeImage = getTreeImage(progress);

                    return (
                        <div key={habit._id} className="progress-tree">
                            <img src={treeImage} alt="Tree progress" className="tree-image" style={{ width: '100px', height: 'auto' }} />
                            <p>{habit.name}</p>
                        </div>
                    );
                })
            )}
        </div>
    );
};

export default ProgressTree;
