import React from 'react';

const ProgressTree = ({ habits }) => {
    // L贸gica para determinar el estado del progreso (puede ser mejorada)
    const getProgressImage = (habit) => {
        const progress = habit.frequency; // Reemplazar con l贸gica real para determinar el progreso
        if (progress < 1) {
            return '/images/progress/seed.png'; // Ruta desde la carpeta 'public'
        } else if (progress < 3) {
            return '/images/progress/sapling.png';
        } else if (progress < 5) {
            return '/images/progress/young_tree.png';
        } else {
            return '/images/progress/tree.png';
        }
    };

    return (
        <div className="progress-tree-container">
            {habits.map((habit) => (
                <img key={habit._id} src={getProgressImage(habit)} alt="Tree Progress" className="progress-tree-image" />
            ))}
        </div>
    );
};

export default ProgressTree;
