import React, { useState } from 'react';
import ProgressTree from './ProgressTree';
import '../styles/mobile.css';
import { updateHabitWorld } from '../services/api'; // Ruta corregida

const HabitCard = ({ habit, onComplete }) => {
    const [selectedWorld, setSelectedWorld] = useState(habit.world || 'forest');

    const handleWorldChange = async (e) => {
        const newWorld = e.target.value;
        setSelectedWorld(newWorld);
        await updateHabitWorld(habit._id, newWorld); // Actualiza el backend
    };

    return (
        <div className="habit-card">
            <h3>{habit.name}</h3>
            <ProgressTree progress={habit.progress} world={selectedWorld} />
            <p>{habit.description}</p>
            <select value={selectedWorld} onChange={handleWorldChange}>
                <option value="forest">Selva</option>
                <option value="city">Ciudad</option>
                <option value="ocean">Oc√©ano</option>
            </select>
            <button className="neomorph-button" onClick={() => onComplete(habit._id)}>
                Completar
            </button>
        </div>
    );
};

export default HabitCard;
