import React, { useEffect, useState } from 'react';
import HabitCard from '../components/HabitCard';
import ProgressTree from '../components/ProgressTree';
import { getHabits, createHabit } from '../services/api';
import BaseLayout from '../components/BaseLayout';
import Navbar from '../components/Navbar';
import '../styles/mobile.css';

const Home = () => {
    const [habits, setHabits] = useState([]);
    const [habitName, setHabitName] = useState('');
    const [error, setError] = useState(null);

    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchHabits = async () => {
            if (token) {
                try {
                    const data = await getHabits(token);
                    setHabits(Array.isArray(data) ? data : []);
                } catch (error) {
                    console.error('Error al obtener los hábitos:', error);
                    setError('Hubo un problema al obtener los hábitos. Inténtalo más tarde.');
                }
            }
        };
        fetchHabits();
    }, [token]);

    const handleCreateHabit = async () => {
        if (habitName.trim()) {
            try {
                const habit = { name: habitName, world: 'forest', frequency: 3 }; // Añadir más datos según sea necesario
                const newHabit = await createHabit(habit, token);
                setHabitName('');
                setHabits((prevHabits) => [...prevHabits, newHabit]);
            } catch (error) {
                console.error('Error al crear el hábito:', error);
                setError('Hubo un problema creando el hábito. Verifica la información ingresada e inténtalo de nuevo.');
            }
        } else {
            alert('Por favor ingrese un nombre para el hábito');
        }
    };

    return (
        <>
            <BaseLayout title="Bienvenido a Tu App de Hábitos">
                {/* Mostrar errores si existen */}
                {error && <div className="error-message">{error}</div>}

                {/* Formulario para crear un hábito */}
                <div className="neomorph-form">
                    <input
                        type="text"
                        className="neomorph-input"
                        value={habitName}
                        onChange={(e) => setHabitName(e.target.value)}
                        placeholder="Nombre del hábito"
                    />
                    <button onClick={handleCreateHabit} className="neomorph-button">
                        Crear Hábito
                    </button>
                </div>

                {/* Visualización del árbol de progreso */}
                {Array.isArray(habits) && habits.length > 0 ? (
                    <ProgressTree habits={habits} />
                ) : (
                    <p>No hay hábitos disponibles. ¡Crea uno para empezar!</p>
                )}

                {/* Lista de hábitos */}
                <div>
                    {Array.isArray(habits) && habits.length > 0 ? habits.map((habit) => (
                        <HabitCard key={habit._id} habit={habit} />
                    )) : <p>No hay hábitos disponibles. ¡Crea uno para empezar!</p>}
                </div>
            </BaseLayout>
            <Navbar />
        </>
    );
};

export default Home;
