import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AiOutlineHome, AiOutlineUser, AiOutlineLogout } from 'react-icons/ai';
import '../styles/navbar.css';

const Navbar = () => {
    const navigate = useNavigate();
    const isAuthenticated = Boolean(localStorage.getItem('token'));

    const handleLogout = () => {
        // Elimina el token y redirige a la página de bienvenida
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login');
        window.location.reload(); // Refresca la aplicación para eliminar el estado
    };

    return (
        <nav className="neomorph-navbar">
            <h1 className="navbar-title">Habit Tracker</h1>
            <div className="navbar-icons">
                {isAuthenticated ? (
                    <>
                        <Link to="/" className="navbar-link">
                            <AiOutlineHome className="navbar-icon" /> 
                        </Link>
                        <Link to="/profile" className="navbar-link">
                            <AiOutlineUser className="navbar-icon" /> 
                        </Link>
                        <button onClick={handleLogout} className="navbar-link">
                            <AiOutlineLogout className="navbar-icon" />
                        </button>
                    </>
                ) : (
                    <>
                        <Link to="/login" className="navbar-link">Iniciar Sesión</Link>
                        <Link to="/register" className="navbar-link">Registrarse</Link>
                    </>
                )}
            </div>
        </nav>
    );
};

export default Navbar;
