import React from 'react';
import '../styles/mobile.css';

const ProgressTree = ({ progress }) => {
    // Determinamos la imagen en funci칩n del progreso
    const growthStage = progress === 100 ? 'tree' : progress >= 50 ? 'sapling' : 'seed';

    // Construimos la ruta de la imagen bas치ndonos en el nombre de la etapa
    const growthImage = `/images/${growthStage}.png`;

    return (
        <div className="neomorph-card">
            {/* Verificamos la ruta de la imagen en consola */}
            <img src={growthImage} alt={growthStage} className="progress-image" />
            <div className="habit-progress-bar">
                <div className="habit-progress-bar-inner" style={{ width: `${progress}%` }}></div>
            </div>
            <p>{progress}% completado</p>
        </div>
    );
};

export default ProgressTree;
