import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
    const navigate = useNavigate();
    const isAuthenticated = Boolean(localStorage.getItem('token'));

    const handleLogout = () => {
        // Elimina el token y redirige a la página de bienvenida
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login'); // Asegura que redirige al usuario a la página de inicio de sesión
        window.location.reload(); // Refresca la aplicación para eliminar el estado
    };

    return (
        <nav>
            <h1>Habit Tracker</h1>
            <div>
                {isAuthenticated ? (
                    <>
                        <Link to="/">Inicio</Link>
                        <Link to="/profile">Perfil</Link>
                        <button onClick={handleLogout}>Cerrar Sesión</button>
                    </>
                ) : (
                    <>
                        <Link to="/login">Iniciar Sesión</Link>
                        <Link to="/register">Registrarse</Link>
                    </>
                )}
            </div>
        </nav>
    );
};

export default Navbar;
