import axios from 'axios';

const API_URL = '/api';

export const registerUser = async (userData) => {
    const response = await axios.post(`${API_URL}/users/register`, userData);
    return response.data;
};

export const loginUser = async (userData) => {
    const response = await axios.post(`${API_URL}/users/login`, userData);
    return response.data;
};

export const getHabits = async (token) => {
    const response = await axios.get(`${API_URL}/habits`, {
        headers: { Authorization: `Bearer ${token}` },
    });
    return response.data;
};

export const completeHabit = async (habitId, token) => {
    const response = await axios.put(`${API_URL}/habits/${habitId}/complete`, {}, {
        headers: { Authorization: `Bearer ${token}` },
    });
    return response.data;
};

export const updateHabitWorld = async (id, world) => {
    const token = localStorage.getItem('token');
    const config = {
        headers: {
            Authorization: `Bearer ${token}`,
        },
    };

    const { data } = await axios.put(`/api/habits/${id}/world`, { world }, config);
    return data;
};
