import React from 'react';
import '../styles/mobile.css';

const VirtualPet = ({ petHealth }) => {
    const petStage = petHealth === 100 ? 'healthy' : petHealth >= 50 ? 'neutral' : 'sick';
    const petImage = `/images/${petStage}.png`;

    return (
        <div className="neomorph-card">
            <h3>Tu Mascota Virtual</h3>
            <img src={petImage} alt="Pet Health" className="pet-image" />
            <p>Salud: {petHealth}%</p>
        </div>
    );
};

export default VirtualPet;
