import React from 'react';
import '../styles/habitCard.css';

const HabitCard = ({ habit, token, onHabitUpdated }) => {
    const handleUpdateWorld = async () => {
        // Lógica para actualizar el mundo del hábito
        try {
            await updateHabitWorld(habit._id, { world: habit.world }, token);
            onHabitUpdated();
        } catch (error) {
            console.error('Error al actualizar el mundo del hábito:', error);
            alert('Hubo un problema al actualizar el mundo del hábito.');
        }
    };

    return (
        <div className="habit-card">
            <h3>{habit.name}</h3>
            <button onClick={handleUpdateWorld} className="update-world-button">
                Actualizar Mundo
            </button>
        </div>
    );
};

export default HabitCard;
